      <iframe
        src={`${this.props.model.link}/embed?autostart=1&preload=1&ui_infos=0`}
        frameborder="0"
        allowvr
        allowfullscreen mozallowfullscreen="true" webkitallowfullscreen="true"
        onmousewheel=""></iframe>
    </div>
  }
})

module.exports = Artwork

