var Markdown = require('./markdown');
var Image = require('./image');

var ArtworkImage = React.createClass({
  displayName: 'ArtworkImage',

  render: function render() {
    var _props = this.props;
    var art = _props.art;
    var id = _props.id;

    var aspectRatio = art.image_width / art.image_height;
    var maxWidth = window.innerWidth ? Math.min(window.innerWidth, 400) : 400;
