  },
  getAffinity: function getAffinity() {
    var _this2 = this;

    var affinities = this.props.departmentInfo.affinityGroups.filter(function (ag) {
      return ag.departments && ag.departments.indexOf(findDepartment(_this2.props.department)[1]) > -1;
    });

    return affinities.map(function (a) {
      var image = React.createElement('img', { src: a.featuredArt ? 'http://api.artsMia.org/images/' + a.featuredArt + '/400/medium.jpg' : a.image });
      image = a.featuredArt ? React.createElement(
        Link,
        { to: 'artwork', params: { id: a.featuredArt } },
        image
      ) : image;

      return React.createElement(
        'div',
        { className: 'affinity', key: a.title },
        React.createElement(
          'h3',
          null,
          a.title
