          React.createElement(
            'p',
            null,
            'Mia\u2019s Affinity Groups are a great way for museum members to connect more closely with special areas of art interest, allowing you to delve deeper into the curatorial area of your choice.'
          ),
          React.createElement(
            'a',
            { href: 'http://new.artsmia.org/join-and-invest/affinity-groups/', className: 'button' },
