              View Details <i className="material-icons">arrow_forward</i>
            </Link>}
        </div>
      </div>
    )
  },

  getUrl() {
    return (
      'https://collections.artsmia.org' + this.context.router.getCurrentPath()
    )
  },

  handleShare() {
    var { art } = this.props

