    const buttonStyles = {
      color: 'white',
      background: '#232323',
      opacity: isLoading ? 0.3 : 1,
    }

    const imgStyle = {
      maxWidth: '100%',
      maxHeight: '300px',
      opacity: isLoading ? 0.3 : 1,
