  displayName: 'ObjectsById',

  mixins: [Router.State],

  statics: {
    fetchData: {
      searchResults: function searchResults(params) {
        return rest(SEARCH + '/ids/' + params.ids).then(function (r) {
          return JSON.parse(r.entity);
        }).then(function (json) {
          // remove any ids that weren't found in ES
          var existingHits = json.hits.hits.filter(function (hit) {
            var hasData = hit && hit.found;
            if (!hasData) console.error('no data for', hit);
            return hasData;
          });

          var ids = existingHits.map(function (hit) {
            return hit._id;
          });

          // and reshape the ES payload to look the same, but exclude them
          return _extends({}, json, {
